import { useDispatch } from 'react-redux';
import React, { Component , useState } from 'react';
import  Upload  from '../upload/Upload'
import ToggleButton from '@mui/material/ToggleButton';
import ToggleButtonGroup from '@mui/material/ToggleButtonGroup';
import ButtonGroup2 from '@mui/material/ButtonGroup';
import Button2 from '@mui/material/Button';
import { toast } from 'react-toastify';
import { Form, 
    Input,  
    Row,
    Col,
    Card,
    Label,
    Radio,
    Button
} from 'antd';
import "./login.css";

import { loginHandler } from '../../functions/auth';
import styled from 'styled-components';






const Login = ({ history }) => {
    const [status, setStatus] = useState({});
    const dispatch = useDispatch();
    const [active, setActive] = useState('upload');
    const types = ['search', 'upload'];
    const [alignment, setAlignment] = React.useState('web');

  const handleChange = (event, newAlignment) => {
    setAlignment(newAlignment);
  };

    const roleBaseRedirect = (role, alignment) => {
        if(role === 'admin' && alignment === 'upload') {
            history.push('/admin/dashboard')
            toast.success('เข้าสู่ระบบสำเร็จ')
        } else if(role === 'admin' && alignment === 'search'){
            history.push('/search')
            toast.success('เข้าสู่ระบบสำเร็จ')
        } else if(role === 'user' && alignment === 'upload'){
            toast.error('สำหรับ admin เท่านั้น')
        } else if(role === 'user' && alignment === 'search'){
            history.push('/search')
            toast.success('เข้าสู่ระบบสำเร็จ')
        } else {
            toast.error('กรุณาเข้าสู่ระบบ')
        }



        
    }

    const ButtonToggle = styled(Button2)`
            opacity: 0.6;
            ${({ active }) =>
                active &&
                `
                opacity: 1;
            `}
            `;


    const onFinish = (values) => {
            values = Object.assign(values, status)
            
            console.log(values,status);
            loginHandler(values)
            .then(res => {
                dispatch({
                    type:'LOGGED_IN_USER',
                    payload:{
                        token: res.data.token,
                        name: res.data.payload.user.name,
                        role: res.data.payload.user.role,
                        alignment: res.data.payload.user.alignment
                    }
                })
                localStorage.setItem('token',res.data.token)
                roleBaseRedirect(res.data.payload.user.role, res.data.payload.user.alignment)
                console.log(res.data.payload.user.role)
                
                //alert(res.data)
                //history.push('/login')
            }).catch(err => {
                toast.error('กรุณาเข้าสู่ระบบ')
                console.log(err.response.data)
                //alert(err.response.data)
            })
          
        };
    
        
    const onFinishFailed = (errorInfo) => {
        console.log('Failed:', errorInfo);
    };


    const onChangeTwiceButton = (e) => {
            const o =  { button : e.target.value }
        setStatus(o)
        //setStatus(e.target.value);
    }


    


    
    
        return(
            <div className="container-center-horizontal">
                <div className="desktop-14 screen">
                    <div className="overlap-group2" >
                        <h1 className="text-6 db-heavent-bold-midnight-blue-36px">
                        ระบบสืบค้นเอกสารอัติโนมัติ สาขาวิทยาการคอมพิวเตอร์ คณะวิทยาศาสตร์
                        </h1>
                        
                        <Form                
                        name="basic"
                        labelCol={{
                            span: 8,
                        }}
                        wrapperCol={{
                            span: 16,
                        }}
                        initialValues={{
                            remember: true,
                        }}
                        onFinish={onFinish}
                        onFinishFailed={onFinishFailed}
                        autoComplete="off"
                        >
                         <ToggleButtonGroup
                            color="primary"
                            value={alignment}
                            exclusive
                            onChange={handleChange}
                            >
                            <ToggleButton value="se">Web</ToggleButton>
                            <ToggleButton value="android">Android</ToggleButton>
                            <ToggleButton value="ios">iOS</ToggleButton>
                            </ToggleButtonGroup>       
                        <ButtonGroup2 
                            onClick={onChangeTwiceButton}
                            className="group-3543 border-1px-bon-jour"
                            fullWidth={true}
                            

                        >
                            
                            {types.map(type => (
                                <ButtonToggle
                                key={type}
                                name="button"
                                active={active === type}
                                onClick={() => setActive(type)}
                                >
                                {type}
                                </ButtonToggle>
                            ))}
                            
                            <ButtonToggle
                                style={{ 
                                   
                                    }}
                                
                        
                                name="button"
                                value="upload"
                            
                            >
                            สำหรับรับเข้า-ส่งออกข้อมูลเอกสาร
                            </ButtonToggle>
                            
                        </ButtonGroup2>


                        <div className="group-3542 db-heavent-regular-normal-black-20px border-1px-bon-jour">
                            
                            <div className="frame-36255">
                                <img
                                    className="bxbxs-lock"
                                />
                                   
                                <div className="text-5 db-heavent-bold-midnight-blue-36px">
                                เข้าสู่ระบบ
                                </div>
                            </div>
                            
                            <div className="group-username">
                                
                            <Form.Item
                                label={ 
                                    <div className="text-1">ชื่อผู้ใช้</div>
                                    }
                                name="name"
                                rules={[
                                {
                                    message: 'Please input your username!',
                                },
                                ]}
                            >
                                <Input />
                            </Form.Item>
                                
                                
                            </div>
                            

                            
                            <div className="group-password">
                            <Form.Item
                                label={ 
                                    <div className="text-1">พาสเวิร์ด</div>
                                    }
                                name="password"
                                rules={[
                                {
                                    message: 'Please input your password!',
                                },
                                ]}
                            >
                                <Input.Password />
                            </Form.Item>
                            </div>
                            


                            <Button 
                                className="ant-btn-primary"
                                htmlType="submit"
                            >เข้าสู่ระบบ</Button>
                            
                            
                        </div>
                        </Form>
                    </div>
                </div>
            </div>
                

        );
  
    }



export default Login;
